import React from "react";
import styled from "styled-components";

const PageContainer = styled.div`
    display: flex;
    min-height: 100vh;
    width: 100vw;
    background: linear-gradient(135deg, #667eea, #764ba2);
    padding: 20px;
    box-sizing: border-box;
    margin-top: 50px; /* Move the content down by 50px (adjust as needed) */
    position: relative; /* Make sure the ProfileSection is positioned relative to this container */
`;

const Sidebar = styled.div`
    width: 25%;
    background: #fff;
    border-radius: 10px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
    padding: 20px;
    margin-right: 20px;
    box-sizing: border-box;
`;

const Content = styled.div`
    flex: 1;
    background: #f7f7f7;
    border-radius: 10px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
    padding: 20px;
    box-sizing: border-box;
`;

const Section = styled.div`
    margin-bottom: 20px;
`;

const Title = styled.h2`
    color: #333;
    font-family: 'Montserrat', sans-serif;
    margin-bottom: 15px;
    font-size: 22px;
    text-align: left;
`;

const Button = styled.button`
    background: #667eea;
    border: none;
    border-radius: 50px;
    color: white;
    padding: 10px 20px;
    cursor: pointer;
    font-size: 16px;
    font-weight: bold;
    margin-top: 10px;
    transition: background 0.3s ease;

    &:hover {
        background: #5a67d8;
    }
`;

const Input = styled.input`
    width: 100%;
    padding: 10px 15px;
    margin: 8px 0;
    border: 1px solid #ccc;
    border-radius: 10px;
    box-sizing: border-box;
`;

const ProfileSection = styled.div`
    position: absolute;
    top: 20px;
    left: 30px;
    background: #000; /* Black background */
    color: #fff;
    border-radius: 10px;
    display: flex;
    align-items: center;
    padding: 10px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
    z-index: 10;
    width: auto; /* Adjust width to fit content */
`;

const ProfileImage = styled.img`
    width: 50px;
    height: 50px;
    border-radius: 50%;
    border: 2px solid #fff;
    margin-right: 10px;
`;

const ProfileDetails = styled.div`
    display: flex;
    flex-direction: column;
`;

const Name = styled.div`
    font-weight: bold;
`;

const Email = styled.div`
    font-size: 14px;
`;

function UploadPage() {
    return (
        <PageContainer>
            <Sidebar>
                <Section>
                    <Title>Previous Reports</Title>
                    {/* Add previous reports elements here */}
                </Section>
                <Section>
                    <Title>Previous Submissions</Title>
                    {/* Add previous submissions elements here */}
                </Section>
                <Section>
                    <Title>Submit GitHub Repository</Title>
                    <Input type="text" placeholder="Enter GitHub URL" />
                    <Button>Submit</Button>
                </Section>
            </Sidebar>
            <Content>
                <Section>
                    <Title>Report Resolve</Title>
                    {/* Add report resolution details here */}
                </Section>
                <Section>
                    <Title>Current Projects</Title>
                    {/* Add current projects details here */}
                </Section>
                <Section>
                    <Title>Upload APK File</Title>
                    <Input type="file" />
                    <Button>Upload & Analyze</Button>
                </Section>
                <Section>
                    <Title>Vulnerabilities Analysis</Title>
                    <ul>
                        <li>Static Analysis</li>
                        <li>Configuration Analysis</li>
                        <li>External Dependencies</li>
                    </ul>
                </Section>
            </Content>
            <ProfileSection>
                <ProfileImage 
                    src="https://via.placeholder.com/50" /* Replace with your profile image URL */
                    alt="Profile"
                />
                <ProfileDetails>
                    <Name>John Doe</Name> {/* Replace with dynamic name */}
                    <Email>john.doe@example.com</Email> {/* Replace with dynamic email */}
                </ProfileDetails>
            </ProfileSection>
        </PageContainer>
    );
}

export default UploadPage;
