import React, { useState } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";

const PageContainer = styled.div`
    display: flex;
    min-height: 100vh;
    width: 100vw;
    background: linear-gradient(135deg, #87CEFA, #764ba2), url('https://media.giphy.com/media/2g8EYDN0VWFMY/giphy.gif') no-repeat center center fixed;
    background-size: cover; /* Ensure the background covers the entire container */
    padding: 20px;
    box-sizing: border-box;
    margin-top: 40px; /* Adjusted to move the entire content down */
    position: relative;
`;


const Sidebar = styled.div`
    width: 25%;
    background: #fff;
    border-radius: 10px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
    padding: 20px;
    margin-right: 20px;
    box-sizing: border-box;
`;

const Content = styled.div`
    flex: 1;
    background: #f7f7f7;
    border-radius: 10px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
    padding: 20px;
    box-sizing: border-box;
`;

const Section = styled.div`
    margin-bottom: 30px; /* Increased to create more space between sections */
`;

const Title = styled.h2`
    color: #333;
    font-family: 'Montserrat', sans-serif;
    margin-bottom: 15px;
    font-size: 22px;
    text-align: left;
`;

const Button = styled(Link)`
    display: block;
    background: #667eea;
    color: white;
    padding: 15px;
    text-align: center;
    border-radius: 5px;
    text-decoration: none;
    margin-top: 10px;
    font-size: 18px;

    &:hover {
        background: #5a67d8;
    }
`;

const Input = styled.input`
    width: 100%;
    padding: 10px 15px;
    margin: 8px 0;
    border: 1px solid #ccc;
    border-radius: 10px;
    box-sizing: border-box;
`;

const ProfileSection = styled.div`
    position: absolute;
    top: 30px;
    left: 30px;
    background: #000; /* Black background */
    color: #fff;
    border-radius: 10px;
    display: flex;
    align-items: center;
    padding: 10px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
    z-index: 10;
    width: auto; /* Adjust width to fit content */
`;

const ProfileImage = styled.img`
    width: 50px;
    height: 50px;
    border-radius: 50%;
    border: 2px solid #fff;
    margin-right: 10px;
`;

const ProfileDetails = styled.div`
    display: flex;
    flex-direction: column;
`;

const Name = styled.div`
    font-weight: bold;
`;

const Email = styled.div`
    font-size: 14px;
`;

const DropdownContainer = styled.div`
    margin-top: 20px;
`;

const DropdownTitle = styled.div`
    display: flex;
    align-items: center;
    cursor: pointer;
    color: #333;
    font-weight: bold;
    font-size: 18px;
    margin-bottom: 10px;
    &:hover {
        color: #667eea; /* Highlight text when hovered */
    }
`;

const DropdownArrow = styled.span`
    margin-right: 10px;
    transform: ${props => (props.isOpen ? "rotate(90deg)" : "rotate(0deg)")};
    transition: transform 0.3s ease;
`;

const DropdownContent = styled.div`
    display: ${props => (props.isOpen ? "block" : "none")};
    padding-left: 20px;
    font-size: 16px;
    background-color: #000; /* Change the background color to black */
    color: #fff; /* Change the text color to white */
    padding: 10px;
    border-radius: 5px;
    margin-top: 10px;
`;

const VulnerabilitiesContainer = styled.div`
    display: flex;
    justify-content: space-between; /* Distribute space between the sections */
    gap: 20px; /* Space between each section */
`;

const VulnerabilitiesSection = styled(Link)`
    background: linear-gradient(135deg, #667eea, #5a67d8);
    color: white;
    padding: 20px;
    border-radius: 10px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
    flex: 1; /* Allow each section to take up equal space */
    max-width: 30%; /* Control the max width of each section */
    text-decoration: none; /* Remove underline from link */
    display: block;
    transition: background 0.3s ease;

    &:hover {
        background: linear-gradient(135deg, #34d399, #10b981); /* Gradient green on hover */
    }
`;

const VulnerabilityTitle = styled.h3`
    font-size: 20px;
    font-weight: bold;
    margin-bottom: 10px;
`;

const VulnerabilityDetails = styled.p`
    font-size: 15px;
    line-height: 1.6;
    margin: 0;
`;

function UploadPage() {
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);

    const toggleDropdown = () => {
        setIsDropdownOpen(!isDropdownOpen);
    };

    return (
        <PageContainer>
            <Sidebar>
                <Section>
                    <Title>Previous Reports</Title>
                    {/* Add previous reports elements here */}
                </Section>
                <Section>
                    <DropdownContainer>
                        <DropdownTitle onClick={toggleDropdown}>
                            <DropdownArrow isOpen={isDropdownOpen}>â–¶</DropdownArrow>
                            Previous Submissions
                        </DropdownTitle>
                        <DropdownContent isOpen={isDropdownOpen}>
                            <p>APK Title 1</p>
                            <p>APK Title 2</p>
                            <p>APK Title 3</p>
                        </DropdownContent>
                    </DropdownContainer>
                </Section>
                <Section>
                    <Title>Submit GitHub Repository</Title>
                    <Input type="text" placeholder="Enter GitHub URL" />
                    <Button to="#">Submit</Button>
                </Section>
            </Sidebar>
            <Content>
                <Section>
                    <Title>Upload APK File</Title>
                    <Input type="file" />
                    <Button to="#">Upload & Analyze</Button>
                </Section>
                <Section>
                    <Title>Vulnerabilities Analysis</Title>
                    <VulnerabilitiesContainer>
                        <VulnerabilitiesSection to="/static-analysis">
                            <VulnerabilityTitle>Static Analysis</VulnerabilityTitle>
                            <VulnerabilityDetails>
                                Static Analysis involves examining the APK file's code and structure without executing it. This process helps to identify potential vulnerabilities like insecure coding practices and potential entry points for attackers.
                            </VulnerabilityDetails>
                        </VulnerabilitiesSection>

                        <VulnerabilitiesSection to="/configuration-analysis">
                            <VulnerabilityTitle>Configuration Analysis</VulnerabilityTitle>
                            <VulnerabilityDetails>
                                Configuration Analysis inspects the APK's configuration files to ensure they are secure. This includes checking for proper permissions, secure communication settings, and other configurations that could impact the app's security.
                            </VulnerabilityDetails>
                        </VulnerabilitiesSection>

                        <VulnerabilitiesSection to="/external-dependencies">
                            <VulnerabilityTitle>External Dependencies</VulnerabilityTitle>
                            <VulnerabilityDetails>
                                External Dependencies analysis reviews the libraries and frameworks used by the APK. It identifies outdated or vulnerable dependencies that could introduce security risks to the app.
                            </VulnerabilityDetails>
                        </VulnerabilitiesSection>
                    </VulnerabilitiesContainer>
                </Section>
            </Content>
            <ProfileSection>
                <ProfileImage 
                    src="/myprofile.jpg" 
                    alt="Profile"
                />
                <ProfileDetails>
                    <Name>Prajwal Weladi</Name> {/* Replace with dynamic name */}
                    <Email>prajwal.weladi22@vit.edu</Email> {/* Replace with dynamic email */}
                    <Email>VIT PUNE</Email>
                </ProfileDetails>
            </ProfileSection>
        </PageContainer>
    );
}

export default UploadPage;
