import React from "react";
import styled from "styled-components";

const PageContainer = styled.div`
    display: flex;
    min-height: 100vh;
    background: linear-gradient(135deg, #667eea, #764ba2);
    padding: 40px;
    box-sizing: border-box;
    max-width: 1600px; /* Set a maximum width to keep the layout manageable on larger screens */
    margin: 0 auto; /* Center the container */
`;

const Sidebar = styled.div`
    width: 30%; /* Increased width to 30% for a broader sidebar */
    background: #fff;
    border-radius: 10px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
    padding: 20px;
    margin-right: 30px; /* Increased margin for more spacing between Sidebar and Content */
`;

const Content = styled.div`
    flex: 1;
    background: #f7f7f7;
    border-radius: 10px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
    padding: 20px;
`;

const Section = styled.div`
    margin-bottom: 30px;
`;

const Title = styled.h2`
    color: #333;
    font-family: 'Montserrat', sans-serif;
    margin-bottom: 20px;
    font-size: 24px;
    text-align: left;
`;

const Button = styled.button`
    background: #667eea;
    border: none;
    border-radius: 50px;
    color: white;
    padding: 10px 25px;
    cursor: pointer;
    font-size: 16px;
    font-weight: bold;
    margin-top: 10px;
    transition: background 0.3s ease;

    &:hover {
        background: #5a67d8;
    }
`;

const Input = styled.input`
    width: 100%;
    padding: 12px 20px;
    margin: 8px 0;
    border: 1px solid #ccc;
    border-radius: 10px;
    box-sizing: border-box;
`;

function UploadPage() {
    return (
        <PageContainer>
            <Sidebar>
                <Section>
                    <Title>Personal Info</Title>
                    {/* Add your personal info form elements here */}
                </Section>
                <Section>
                    <Title>Previous Reports</Title>
                    {/* Add previous reports elements here */}
                </Section>
                <Section>
                    <Title>Previous Submissions</Title>
                    {/* Add previous submissions elements here */}
                </Section>
                <Section>
                    <Title>Submit GitHub Repository</Title>
                    <Input type="text" placeholder="Enter GitHub URL" />
                    <Button>Submit</Button>
                </Section>
            </Sidebar>
            <Content>
                <Section>
                    <Title>Report Resolve</Title>
                    {/* Add report resolution details here */}
                </Section>
                <Section>
                    <Title>Current Projects</Title>
                    {/* Add current projects details here */}
                </Section>
                <Section>
                    <Title>Upload APK File</Title>
                    <Input type="file" />
                    <Button>Upload & Analyze</Button>
                </Section>
                <Section>
                    <Title>Vulnerabilities Analysis</Title>
                    <ul>
                        <li>Static Analysis</li>
                        <li>Configuration Analysis</li>
                        <li>External Dependencies</li>
                    </ul>
                </Section>
            </Content>
        </PageContainer>
    );
}

export default UploadPage;
