import React from "react";
import { BrowserRouter as Router, Route, Routes, useNavigate } from "react-router-dom";
import * as Components from './Components';
import UploadPage from './UploadPage';
import StaticAnalysis from './StaticAnalysis';
import ConfigurationAnalysis from './ConfigurationAnalysis';
import ExternalDependencies from './ExternalDependencies';
import styled from "styled-components";

const Background = styled.div`
    background: linear-gradient(135deg, #1E1E2E, #3B3B58);
    height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
    text-align: center;
    color: white;
`;

const Title = styled.h1`
    font-size: 2.5rem;
    font-weight: bold;
    text-transform: uppercase;
    margin-bottom: 20px;
`;

const Paragraph = styled.p`
    font-size: 1.2rem;
    opacity: 0.8;
    max-width: 400px;
    margin: auto;
`;

const GlowButton = styled(Components.Button)`
    background: linear-gradient(135deg, #667eea, #764ba2);
    box-shadow: 0px 0px 10px rgba(118, 75, 162, 0.8);
    transition: all 0.3s ease-in-out;
    &:hover {
        box-shadow: 0px 0px 20px rgba(118, 75, 162, 1);
    }
`;

function Home() {
    const [signIn, toggle] = React.useState(true);
    const navigate = useNavigate();

    return (
        <Background>
            <Components.Container>
                <Components.SignUpContainer signinIn={signIn}>
                    <Components.Form>
                        <Title>Create Account</Title>
                        <Components.Input type='text' placeholder='Name' />
                        <Components.Input type='email' placeholder='Email' />
                        <Components.Input type='password' placeholder='Password' />
                        <GlowButton onClick={() => navigate('/upload')}>
                            Sign Up
                        </GlowButton>
                    </Components.Form>
                </Components.SignUpContainer>

                <Components.SignInContainer signinIn={signIn}>
                    <Components.Form>
                        <Title>Sign In</Title>
                        <Components.Input type='email' placeholder='Email' />
                        <Components.Input type='password' placeholder='Password' />
                        <Components.Anchor href='#'>Forgot your password?</Components.Anchor>
                        <GlowButton>
                            Sign In
                        </GlowButton>
                    </Components.Form>
                </Components.SignInContainer>

                <Components.OverlayContainer signinIn={signIn}>
                    <Components.Overlay signinIn={signIn}>
                        <Components.LeftOverlayPanel signinIn={signIn}>
                            <Title>Welcome Back!</Title>
                            <Paragraph>
                                To keep connected with us please login with your personal info
                            </Paragraph>
                            <GlowButton onClick={() => toggle(true)}>
                                Sign In
                            </GlowButton>
                        </Components.LeftOverlayPanel>

                        <Components.RightOverlayPanel signinIn={signIn}>
                            <Title>Hello, Friend!</Title>
                            <Paragraph>
                                Enter your personal details and start your journey with us
                            </Paragraph>
                            <GlowButton onClick={() => toggle(false)}>
                                Sign Up
                            </GlowButton>
                        </Components.RightOverlayPanel>
                    </Components.Overlay>
                </Components.OverlayContainer>
            </Components.Container>
        </Background>
    );
}

function App() {
    return (
        <Router>
            <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/upload" element={<UploadPage />} />
                <Route path="/static-analysis" element={<StaticAnalysis />} />
                <Route path="/configuration-analysis" element={<ConfigurationAnalysis />} />
                <Route path="/external-dependencies" element={<ExternalDependencies />} />
            </Routes>
        </Router>
    );
}

export default App;
